import React from 'react';
// import { connect } from 'react-redux'

import Button from '@material-ui/core/button'

class Home extends React.Component {
  state = { is_favorites: false }

  toFav() {
    this.setState(prev => ({
      is_favorites: !prev.is_favorites}));
  }

  render() {
    return (
      <div>
        <h1>{this.state.is_favorites ? "Favorites baby" : "Not in Favorites"}</h1>
        Hello this is the weather app home
        <Button type="button" onClick={this.toFav} color="primary">
          press me honey!
        </Button>
      </div>
    );
  }
}

