import React from 'react';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
// import HeartIcon from '@material-ui/icons/Heart';

import { getCitiesList} from '../weatherAPI';
import WeatherCard from './WeatherCard';

class WeatherDetails extends React.Component {

  default_location = 215854;
  state = { cityCode: this.default_location };

  handleEnter(e) {
    if (e.key === 'Enter') {
      e.preventDefault();
      this.setState({
        cityCode: this.getCityKey(document.getElementById('loc_input').value)
      });
      console.log(this.state.cityCode);
    };
  }

  createCityString(city) {
    return `${city.LocalizedName}, ${city.AdministrativeArea.LocalizedName}, ${city.Country.LocalizedName}`;
  }

  getCityKey(city_name) {
    var cities_list = getCitiesList(city_name);
    for (var city in cities_list) {
      console.log(city);
      if (city_name === this.createCityString(city.value)) {
        return city.key;
      }
    }
  }

  render() {
    console.log("Weather details");
    return (
      <div>
        <FormControl fullWidth >
          <InputLabel>Location</InputLabel>
          <Input 
            id="loc_input" 
            onChange={() => getCitiesList(document.getElementById('loc_input').value)}
            onKeyDown={(e) => this.handleEnter(e)}
          />
        </FormControl>
        <WeatherCard code={this.state.cityCode}/>
      </div>
    );
  }
}

export default WeatherDetails;
