import React from 'react';
// import { connect } from 'react-redux'

import Button from '@material-ui/core/Button';

class Home extends React.Component {
  
  constructor(props){
    super(props);
    this.state = { is_favorites: false };

    this.toFav = this.toFav.bind(this);
  }

  toFav() {
    this.setState({
      is_favorites: !this.state.is_favorites});
  }

  render() {
    return (
      <div>
        <h1>{this.state.is_favorites ? "Favorites baby" : "Not in Favorites"}</h1>
        Hello this is the weather app home
        <Button type="button" onClick={this.toFav}>
          press me honey!
        </Button>
      </div>
    );
  }
}

