import React from 'react';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import Grid from '@material-ui/core/Grid';
import { Typography, Paper } from "@material-ui/core";

import { getCitiesList, requestWeatherDetails } from '../weatherAPI';

class WeatherDetails extends React.Component {

  default_location = 215854;
  state = { weatherData: '' };

  handleEnter(e) {
    if (e.key === 'Enter') {
      this.setState({
        weatherData: requestWeatherDetails(this.getCityKey(document.getElementById('loc_input').value))
      });
      console.log(this.state.weatherData);
    };
  }

  createCityString(city) {
    return `${city.LocalizedName}, ${city.AdministrativeArea.LocalizedName}, ${city.Country.LocalizedName}`;
  }

  getCityKey(city_name) {
    var cities_list = getCitiesList(city_name);
    for (var city in cities_list) {
      console.log(city);
      if (city_name === this.createCityString(city.value)) {
        return city.key;
      }
    }
  }

  render() {
    console.log("Weather details");
    return (
      <div>
        <FormControl fullWidth >
          <InputLabel>Location</InputLabel>
          <Input 
            id="loc_input" 
            onChange={() => getCitiesList(document.getElementById('loc_input').value)}
            onKeyDown={(e) => this.handleEnter(e)}
          />
        </FormControl>
        <Grid>
          <Paper >
            <Grid container spacing={2}>
              <Grid item xs={12} sm container>
                <Grid item xs container direction="column" spacing={2}>
                  <Grid item xs>
                    <Typography gutterBottom variant="subtitle1">
                      Standard license
                    </Typography>
                    <Typography variant="body2" gutterBottom>
                      Full resolution 1920x1080 â€¢ JPEG
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      ID: 1030114
                    </Typography>
                  </Grid>
                  <Grid item>
                    <Typography variant="body2" style={{ cursor: 'pointer' }}>
                      Remove
                    </Typography>
                  </Grid>
                </Grid>
                <Grid item>
                  <Typography variant="subtitle1">$19.00</Typography>
                </Grid>
              </Grid>
            </Grid>
          </Paper>
        </Grid>
      </div>
    );
  }
}

export default WeatherDetails;
