import React from 'react';
// import { connect } from 'react-redux'

import Button from '@material-ui/core/Button';
import AppBar from '@material-ui/core/AppBar';
import ToolBar from '@material-ui/core/Toolbar';


class Home extends React.Component {
  
  constructor(){
    super();
    this.state = { is_favorites: false };

    this.toFav = this.toFav.bind(this);
  }

  toFav() {
    this.setState({
      is_favorites: !this.state.is_favorites});
  }

  render() {
    return (
      <div className="div-top">
        <AppBar> 
          <ToolBar>
            <h1>{this.state.is_favorites ? "Favorites baby" : "Not in Favorites"}</h1>
            <div style={{ margin_left: "15px" }}>Hello this is the weather app home</div>
            <button onClick={this.toFav}>
              press me honey!
            </button>
          </ToolBar>
        </AppBar>
      </div>
    );
  }
}

// const mapStateToProps = state => {
//   return { is_favorites: state.auth.is_favorites }
// }

// export default connect(
//   mapStateToProps,
//   {}
// )(Home)

export default Home
