import React from 'react';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography'

import { requestFiveDayPrediction } from "../weatherAPI";
import Prediction from './Prediction';
import '../styles.css';

class MultiplePredictions extends React.Component {

  state = { mul: '', forcast: ''};

  generateData() {
    var predictions_list = [];
    var multiple_forcast = requestFiveDayPrediction(this.props.code);
    console.log(multiple_forcast);
    if (this.multiple_forcast) {
      predictions_list = multiple_forcast.DailyForecasts.map((daily_data, index) => {
        return <Prediction data={daily_data} key={index}/>;
      })
    }
    return predictions_list;
  }

  render() {
    console.log(this.props);
    const multiple_forcast = requestFiveDayPrediction(this.props.code).then((response) => { this.setState({headline: response.Headline, forcast: response.DailyForecasts }) });
    return(
      <div>
        <Grid container spacing={2}>
          <Typography>
            TExt
          </Typography>
          <Grid item xs={12}>
            <Grid container justify="space-around">
              {this.state.forcast.map(value => (
                <Grid key={value} item>
                  <Paper className="paper"/>
                  <Typography>
                    {value.Temperature.Min}
                  </Typography>
                </Grid>
              ))}
            </Grid>
          </Grid>
        </Grid>
      </div>
    );
  }
}

export default MultiplePredictions;
