import React from 'react';
import { connect } from 'react-redux';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';

import { getCitiesList, requestWeatherDetails } from '../weatherAPI';

class WeatherDetails extends React.Component {

  default_location = 215854;
  state = { weatherData: '' };

  render() {
    console.log("Weather details");
    console.log(requestWeatherDetails(328328));
    return (
      <div>
        <FormControl onSubmit={() => requestWeatherDetails(this.getCityList(document.getElementById('loc_input').value))} fullWidth >
          <InputLabel>Location</InputLabel>
          <Input id="loc_input" onChange={() => getCitiesList(document.getElementById('loc_input').value)} />
        </FormControl>
      </div>
    );
  }
}

export default connect(null, { getCitiesList, requestWeatherDetails })(WeatherDetails);
