import React from 'react';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import HeartIcon from '@material-ui/icons/Heart';

import { getCitiesList, requestWeatherDetails } from '../weatherAPI';
import MultiplePredictions from './MultiplePredictions';

class WeatherDetails extends React.Component {

  default_location = 215854;
  state = { weatherData: '' };

  handleEnter(e) {
    if (e.key === 'Enter') {
      this.setState({
        weatherData: requestWeatherDetails(this.getCityKey(document.getElementById('loc_input').value))
      });
      console.log(this.state.weatherData);
    };
  }

  createCityString(city) {
    return `${city.LocalizedName}, ${city.AdministrativeArea.LocalizedName}, ${city.Country.LocalizedName}`;
  }

  getCityKey(city_name) {
    var cities_list = getCitiesList(city_name);
    for (var city in cities_list) {
      console.log(city);
      if (city_name === this.createCityString(city.value)) {
        return city.key;
      }
    }
  }

  render() {
    console.log("Weather details");
    return (
      <div>
        <FormControl fullWidth >
          <InputLabel>Location</InputLabel>
          <Input 
            id="loc_input" 
            onChange={() => getCitiesList(document.getElementById('loc_input').value)}
            onKeyDown={(e) => this.handleEnter(e)}
          />
        </FormControl>
        <MultiplePredictions />
      </div>
    );
  }
}

export default WeatherDetails;
