import React from 'react';
import './SeasonDisplay.css';

import WbSunnyIcon from '@material-ui/icons/WbSunny';
import AcUnitIcon from '@material-ui/icons/Add';

const seasonConfig = {
  summer: {
    text: "Let's hit the beach",
    iconName: "sun"
  },
  winter: {
    text: "Burrr it's chilly",
    iconName: "snowflake"
  }
};

const getSeason = (lat, month) => {
  if (month > 2 && month < 9) {
    return lat > 0 ? 'summer' : 'winter';
  } else {
    return lat > 0 ? 'winter' : 'summer';
  }
}

const SeasonDisplay = (props) => {
  const season = getSeason(props.lat, new Date().getMonth());
  const { text, iconName } = seasonConfig[season];

  return (
    <div className={`season-display ${season}`}>
      {iconName === "sun" ? <WbSunnyIcon className={"icon-left"} fontSize="large"/> : <AcUnitIcon className={"icon-left"} fontSize="large"/>}
      <h1>{text}</h1>
      {iconName === "sun" ? <WbSunnyIcon className={"icon-right"} fontSize="large"/> : <AcUnitIcon className={"icon-right"} fontSize="large"/>}
    </div>
  );
};

export default SeasonDisplay;
