import React from 'react';
import Card from '@material-ui/core/Card';

import Prediction from './Weather/Prediction';
import { requestWeatherDetails } from '../weatherAPI';
import '../styles.css';

class Favorites extends React.Component{
  
  state = { fav_data: {} };

  async componentDidMount() {
    const forecasts = await this.props.favorite_list.forEach((city) => {
      return requestWeatherDetails(city.code);
    });
    this.setState({
      fav_data: forecasts,
    });
  }

  getFavData() {
    if (this.props.favorite_list.length) {
      return (
        <div className="card-div">
          { this.props.favorite_list.map( (fav_city) => {
          return (
            <Card className="card">
              <Prediction data={fav_city} />
            </Card>
          );
          })} 
        </div>
      );
    }
  }

  render() {
    console.log(this.props.favorite_list);
    return (
      <div>
        {this.getFavData()}
      </div>
    );
  }
}

export default Favorites;
