import React from 'react';
import axios from "axios";
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';



class WeatherDetails extends React.Component {

  apiKey = 'xJ4I5DWKpOU1XHmK1ZrDmIkgfffyLX3s';
  state = { weatherData: '' };

  requestWeatherDetails(city_code) {
    console.log("weather bitches");
    axios.get(`http://dataservice.accuweather.com/currentconditions/v1/${city_code}.json`, {
      params: {
        apikey: this.apiKey
      }
    }).then(({ data }) => {
      this.setState({ weatherData: data });
      console.log(data);
    });
  }

  getCityList(city_name) {
    console.log("getting city list")
    axios.get(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete`, {
      params: {
        apikey: this.apiKey,
        q: city_name
      }
    }).then(({ data }) => {
      console.log(data)
      return data; //filter the data to key, localizedName (see console)
    });
  }


  render() {
    console.log("Weather details");
    return (
      <div>
        <FormControl onSubmit={() => this.requestWeatherDetails(328328)} fullWidth >
          <InputLabel>Location</InputLabel>
          <Input id="loc_input" onChange={() => this.getCityList(document.getElementById('loc_input').value)} />
        </FormControl>
      </div>
    );
  }
}

export default WeatherDetails;
