import React from 'react';
import Card from '@material-ui/core/Card';
import IconButton from '@material-ui/core/IconButton';
import FavoriteIcon from '@material-ui/icons/Favorite';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';
import Typography from '@material-ui/core/Typography';

import Prediction from './Prediction';
import { requestFiveDayPrediction } from '../weatherAPI';
import '../styles.css';

class WeatherCard extends React.Component {

  state = { favStat: false, futureData: [], createForecast: false};

  async componentDidMount() {
    const forecast = await requestFiveDayPrediction(this.props.code);
    console.log(forecast);
    var create = forecast.Headline ? true : false
    this.setState({
      futureData: forecast,
      createForecast: create
    });
  }

  changeFavStat() {
    this.setState({ favStat: !this.state.favStat});
    this.props.callback(this.props.code, this.state.favStat);
  }

  getFutureforecast() {
    console.log(this.state);
    if (this.state.futureData.DailyForecasts !== undefined) {
      return this.state.futureData.DailyForecasts.map( (day) => {
        return (
          <Card>
            <Prediction data={day} />
          </Card>
        );
      }); 
    }
    return "oopsy";
  }

  render() {
    console.log(this.props);
    console.log(`${this.getFutureforecast()}`)
    return (
      <div>
        <Card>
          <Typography>
            {this.props.city_name}
            {this.state.futureData.DailyForecasts}
          </Typography>
          <IconButton className="fav-icon" onClick={() => this.changeFavStat()}>
            {this.state.favStat ? <FavoriteIcon color="primary"/> : <FavoriteBorderIcon color="primary"/>}
          </IconButton>
          <Typography>{ this.state.createForecast ? this.state.futureData.Headline.Text : null }</Typography>
          {/* {this.state.createForecast ? this.getFutureforecast() : <Typography>Loading...</Typography>} */}
        </Card>
      </div>
    );
  }
}

export default WeatherCard;
