import React from 'react';
import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';

import { getCitiesList } from '../../weatherAPI';
import '../../styles.css';

class Search extends React.Component {

  state = { cities_list: { } };

  async componentDidUpdate() {
    this.setState({
      cities_list: await getCitiesList(this.props.name)
    });
  }

  displayOptions() {
    return this.state.cities_list.map((city) => {
      var key = city.split('/')[0];
      var name = city.split('/')[1];
      return (
        <Typography key={key}>{name}</Typography>
      )
    })
  }

  render() {
    console.log(this.props);
    return (
      <Card>
        {this.displayOptions()}
      </Card>
    );
  }
}

export default Search;
