import React from 'react';
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import { FormControl, InputLabel, Input } from '@material-ui/core';



class WeatherDetails extends React.Component{

  apiKey = 'xJ4I5DWKpOU1XHmK1ZrDmIkgfffyLX3s';
  state = { weatherData : '' };

  requestWeatherDetails(city_code) {
    console.log("weather bitches");
    axios.get(`http://dataservice.accuweather.com/currentconditions/v1/${city_code}.json`,{
      params: {
        apikey: this.apiKey
      }
    }).then(({data}) => {
      this.setState({ weatherData: data});
      console.log(data);
    });
  }

  getCityList(city_name) {
    console.log("getting city list")
    axios.get(`http://dataservice.accuweather.com/currentconditions/v1/${city_name}.json`,{
      params: {
        apikey: this.apiKey
      }
    }).then(({data}) => {
      return data;
    });
  }


  render() {
    console.log("Weather details");
    return (
      <div>
        <FormControl onSubmit={() => this.requestWeatherDetails(328328)} fullWidth >
          <InputLabel>Location</InputLabel>
          <Input onChange={this.getCityList}/>
        </FormControl>
      </div>
    );
  }
}

export default WeatherDetails;
