import React from 'react';
import Card from '@material-ui/core/Card';

import Prediction from './Weather/Prediction';
import { requestWeatherDetails } from '../weatherAPI';
import '../styles.css';

class Favorites extends React.Component{
  
  state = { fav_data: {} };

  async componentDidMount() {
    if (!this.isEmpty(this.props.favorite_list)) {
      const forecasts = await this.props.favorite_list.map( async (city_code, index) => {
        return await requestWeatherDetails(city_code);
      });
      this.setState({
        fav_data: forecasts,
      });
    }
  }

  isEmpty(obj) {
    if (obj == null) return true;
    if (obj.length > 0)    return false;
    if (obj.length === 0)  return true;
    if (typeof obj !== "object") return true;
    for (var key in obj) {
      if (hasOwnProperty.call(obj, key)) return false;
    }
    return true;
  }

  getFavData() {
    if (this.state.fav_data.length) {
      return (
        <div className="card-div">
          { this.state.fav_data.map((fav_city) => {
            return (
              <Card className="card">
                <Prediction data={fav_city} is_from_fav={true}/>
              </Card>
            );
          })} 
        </div>
      );
    }
  }

  render() {
    return (
      <div>
        {this.getFavData()}
      </div>
    );
  }
}

export default Favorites;
