import React from 'react';
import { connect } from 'react-redux';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';


import { getCitiesList, requestWeatherDetails } from '../weatherAPI';

class WeatherDetails extends React.Component {

  default_location = 215854;
  state = { weatherData: '' };

  handleEnter(e) {
    if (e.key === 'Enter') {
      this.setState({
        weatherData: requestWeatherDetails(this.getCity(document.getElementById('loc_input').value))
      });
    };
  }

  createCityString(city) {
    return `${city.LocalizedName}, ${city.AdministrativeArea.LocalizedName}, ${city.Country.LocalizedName}`;
  }

  getCity(city_name) {
    var cities_list = getCitiesList(city_name);
    cities_list.map((city, index) => {
      if (city_name === this.createCityString(city)) {
        return city.Key;
      }  
    })
    return 0;
  }

  render() {
    console.log("Weather details");
    console.log(requestWeatherDetails(328328));
    return (
      <div>
        <FormControl fullWidth >
          <InputLabel>Location</InputLabel>
          <Input 
            id="loc_input" 
            onChange={() => getCitiesList(document.getElementById('loc_input').value)}
            onKeyDown={(e) => this.handleEnter(e)}
          />
        </FormControl>
      </div>
    );
  }
}

export default connect(null, { getCitiesList, requestWeatherDetails })(WeatherDetails);
