import React from 'react';
import Card from '@material-ui/core/Card';
import IconButton from '@material-ui/core/IconButton';
import FavoriteIcon from '@material-ui/icons/Favorite';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';
import Typography from '@material-ui/core/Typography';

import { requestFiveDayPrediction } from '../weatherAPI';
import '../styles.css';

class WeatherCard extends React.Component {

  state = { favStat: false, futureData: undefined};

  componentDidMount() {
    const forecast = requestFiveDayPrediction(this.props.code);
    console.log(forecast);
    this.setState({
      futureData: forecast
    });
  }

  changeFavStat() {
    this.setState({ favStat: !this.state.favStat});
    this.props.callback(this.props.code, this.state.favStat);
  }

  getFutureforecast() {
    if (!this.state.futureData.length) {
      return <div>Loading...</div>;
    }
    return this.state.futureData.forEach(day => {
      return day.Temperature.Minimum.value; 
    }); 
  }

  render() {
    console.log(this.props);
    return (
      <div>
        <Card>
          <Typography>City_Name Temperature </Typography>
          <IconButton className="fav-icon" onClick={() => this.changeFavStat()}>
            {this.state.favStat ? <FavoriteIcon color="primary"/> : <FavoriteBorderIcon color="primary"/>}
          </IconButton>
          {this.getFutureforecast()}
        </Card>
      </div>
    );
  }
}

export default WeatherCard;
