import axios from 'axios';

const apiKey = 'xJ4I5DWKpOU1XHmK1ZrDmIkgfffyLX3s';
 
export function requestWeatherDetails(city_code) {
  if (city_code !== null && city_code !== undefined) {
    console.log("weather bitches");
    axios.get(`http://dataservice.accuweather.com/currentconditions/v1/${city_code}.json`, {
      params: {
        apikey: apiKey
      }
    }).then(({ data }) => {
      console.log(data);
      return data;
    });
  }
  return {};
}

export function requestFiveDayPrediction(city_code) {
  if (city_code !== null && city_code !== undefined) {
    console.log("weather bitches");
    axios.get(`http://dataservice.accuweather.com/forecasts/v1/daily/5day/${city_code}.json`, {
      params: {
        apikey: apiKey,
        metric: true
      }
    }).then(({ data }) => {
      console.log(data);
      return data;
    });
  }
  console.log("nop");
  return {};
}

export function getCitiesList(city_name) {
  var new_data = {};
  if (city_name !== null && city_name !== undefined && city_name !== "") {
    console.log("getting city list")
    axios.get(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete`, {
      params: {
        apikey: apiKey,
        q: city_name
      }
    }).then(({ data }) => {
      console.log(data)
      //filter the data to key, localizedName (see console)    
      data.map((city, index) => {
        new_data[city.Key] = `${city.LocalizedName}, ${city.AdministrativeArea.LocalizedName}, ${city.Country.LocalizedName}`;
      })
      console.log(new_data);
    });
  }
  return new_data;
}
