import axios from 'axios';
import { combineReducers } from 'redux';

const API_KEY = 'xJ4I5DWKpOU1XHmK1ZrDmIkgfffyLX3s';

export function makeApiRequest() {

}

function requestWeatherDetails(city_code) {
  axios.get(`http://dataservice.accuweather.com/currentconditions/v1/${city_code}.json`, {
    params: {
      apikey: API_KEY
    }
  }).then(({ data }) => {
    this.setState({ weatherData: data });
    console.log(data);
    return data;
  });
  return;
}

function getCityList(city_name) {
  var new_data = {};  
  console.log("getting city list");
  axios.get(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete`, {
    params: {
      apikey: API_KEY,
      q: city_name
    }
  }).then(({ data }) => {
    console.log(data)
    //filter the data to key, localizedName (see console)
    data.map((city, index) => {
      new_data[city.Key] = city.LocalizedName + ", " + city.Country.LocalizedName;
    })
    console.log(new_data);
  });
  return new_data;
}

export default combineReducers ({
  requestWeather: requestWeatherDetails,
  getOptions: getCityList
});
