import React from 'react';

import { requestFiveDayPrediction } from "../weatherAPI";
import Prediction from './Prediction';

class MultiplePredictions extends React.Component {

  readTextFile(file)
  {
    var rawFile = new XMLHttpRequest();
    rawFile.open("GET", file, false);
    rawFile.onreadystatechange = function ()
    {
        if(rawFile.readyState === 4)
        {
            if(rawFile.status === 200 || rawFile.status === 0)
            {
                var allText = rawFile.responseText;
            }
        }
    }
    rawFile.send(null);
    console.log(rawFile);
  }

  generateData() {
    //var multiple_forcast = requestFiveDayPrediction(this.props.code);
    var multiple_forcast = this.readTextFile("src/5DayResponse.json");
    console.log(multiple_forcast);
    if (this.multiple_forcast) {
      return multiple_forcast["DailyForecasts"].map((daily_data, index) => {
        return <Prediction data={daily_data} key={index}/>;
      })
    }
    return null;
  }

  render() {
    const data = this.generateData();
    console.log(data);
    function Comp () { this.generateData() };
    return(
      <div>
        Multiple Predictions
      </div>
    );
  }
}

export default MultiplePredictions;
