import axios from 'axios';

const apiKey = 'xJ4I5DWKpOU1XHmK1ZrDmIkgfffyLX3s';
 
export function requestWeatherDetails(city_code) {
  console.log("weather bitches");
  axios.get(`http://dataservice.accuweather.com/currentconditions/v1/${city_code}.json`, {
    params: {
      apikey: apiKey
    }
  }).then(({ data }) => {
    console.log(data);
  });
}

export function getCitiesList(city_name) {
  console.log("getting city list")
  axios.get(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete`, {
    params: {
      apikey: apiKey,
      q: city_name
    }
  }).then(({ data }) => {
    console.log(data)
    //filter the data to key, localizedName (see console)
    var new_data = {};
    data.map((city, index) => {
      new_data[city.Key] = city.LocalizedName + ", " + city.Country.LocalizedName;
    })
    console.log(new_data);
  });
}
