import React from 'react';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
// import HeartIcon from '@material-ui/icons/Heart';

import { getCitiesList } from '../../weatherAPI';
import WeatherCard from './WeatherCard';
import '../../styles.css';

class WeatherDetails extends React.Component {

  state = { cityCode: 215854 , cityName: 'Tel Aviv'}; //Tel Aviv code - default location code

  handleEnter(e) {
    if (e.key === 'Enter') {
      e.preventDefault();
      this.setState({
        cityCode: this.getCityKey(document.getElementById('loc_input').value)
      });
      console.log(this.state.cityCode);
    }
  }

  createCityString(city) {
    return `${city.LocalizedName}, ${city.AdministrativeArea.LocalizedName}, ${city.Country.LocalizedName}`;
  }

  async getCityKey(city_name) {
    var cities_list = await getCitiesList(city_name);
    console.log(cities_list);
    for (var city in cities_list) {
      console.log(city);
      if (city_name === this.createCityString(city.value)) {
        return city.key;
      }
    }
  }

  render() {
    return (
      <div>
        <FormControl className="search-bar">
          <InputLabel>Location</InputLabel>
          <Input 
            id="loc_input" 
            onChange={() => getCitiesList(document.getElementById('loc_input').value)}
            onKeyDown={(e) => this.handleEnter(e)}
          />
        </FormControl>
        <WeatherCard code={this.state.cityCode} city_name={this.state.cityName} callback={this.props.callback}/>
      </div>
    );
  }
}

export default WeatherDetails;
