import React from 'react';
import ReactDOM from 'react-dom';
import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';

import './styles.css';
import weatherDetails from './components/weatherDetails';
import Favorites from './components/Favorites';

class App extends React.Component {

  state = { is_favorites: false, errMessage: ''};

  changeView(is_fav) {
    if (this.state.is_favorites !== is_fav) {
      this.setState({ is_favorites: !this.state.is_favorites });
    }
  }

  render() {
    return (
      <div id="app-bar-div"> 
        <AppBar>
          <Toolbar className="tool-bar">
            <Typography variant="h6">
              Herolo Weather Task
            </Typography>
            <div className="buttons-div">
              <Button className="app-bar-buttons" onClick={() => this.changeView(false)}>Home</Button>
              <Button className="app-bar-buttons fav-button" onClick={() => this.changeView(true)}>Favorites</Button>
            </div>
          </Toolbar>
        </AppBar>
        {this.state.is_favorites ? <Favorites /> : <weatherDetails />}
      </div>
    );
  }
}

ReactDOM.render(<App />,document.querySelector('#root'));
