  state = { weatherData : '' };

  getWeather() {
    axios.get('GET http://dataservice.accuweather.com/currentconditions/v1/',{
      params: {
        apikey: this.apiKey
      }
    }).then(({data}) => {
      this.setState({ weatherData: data});
